function check_if_in_view(){
    var i=$window.height(),e=$window.scrollTop(),n=e+i;$.each($animation_elements,function(){
        var i=$(this),
        t=i.outerHeight(),
        o=i.offset().top;
        o+t>=e&&o<=n?i.addClass("in-view"):i.removeClass("in-view")})}
        var $animation_elements=$(".animation-element"),
        $window=$(window);
        $window.on("scroll resize",
        check_if_in_view),
        $window.trigger("scroll"),
        $(document).ready(function(){
            $("#list").click(function(i){
                i.preventDefault(),
                $("#products .item").addClass("list-group-item")}),
                $("#grid").click(function(i){i.preventDefault(),
                    $("#products .item").removeClass("list-group-item"),
                    $("#products .item").addClass("grid-group-item")})}),
                    $(function(){new Swiper(".swiper-container",
                    {loop:!0,pagination:".swiper-pagination",
                    paginationClickable:!0,
                    nextButton:".swiper-button-next",prevButton:".swiper-button-prev"})});